mapper.select:
select count(id) from problem_result
where user_id = #{userId}
and problem_id = #{problemId}
and status = #{status}

select count(id) from problem_result
where user_id = #{userId}
and problem_id = #{problemId}

select
p.id, user_id, u.name, problem_id, comp_id, status, type, time, memory,
p.create_time
from problem_result p
join user u on u.id = p.user_id


    problem_id = #{problemId}


    and u.name like concat('%',#{name},'%')


    and type = #{type}


    and status = #{status}


order by p.create_time desc

select pr.id, user_id, problem_id, comp_id, run_num, pr.status, type, pr.time, pr.memory, error_msg, source_code, pr.create_time, pr.update_time, u.avatar,u.name,p.name problem_name, tr.id t_id,tr.pro_re_id,tr.num t_num, tr.user_output,tr.time t_time,tr.memory t_memory,tr.status t_status
from problem_result pr join user u on u.id = pr.user_id join problem p on p.id = pr.problem_id left join testcase_result tr on tr.pro_re_id = pr.id
where pr.id = #{id}

select pr.id, pr.user_id, pr.problem_id, pr.comp_id, pr.run_num, pr.status, pr.type, pr.time, pr.memory, pr.create_time, pr.update_time,
p.name problem_name
from problem_result pr
join problem p on p.id = pr.problem_id
where pr.user_id = #{userId}
and pr.comp_id = #{compId}

select sum(score) from
  (select max(comp_score) score from problem_result pr
              where user_id = #{userId} and comp_id = #{compId} group by problem_id) as t;
